// 常用方法对象
// 作者：吴岸标
var Common = {};

// 删除两端的空白
Common.trim = function(str) {
    ///	<summary>
    ///		删除两端的空白 
    ///	</summary>

    return str.replace(/(^\s*)|(\s*$)/g, "");
}

//返回汉字的拼音的第一个字母
Common.GetPY = function(str) {
    ///	<summary>
    ///		返回汉字的拼音的第一个字母
    ///	</summary>

    var ret = '';
    for (var i = 0, len = str.length; i < len; i++) {
        var ch = str.charAt(i);
        ret += CheckCh(ch);
    }

    return ret;
}

function CheckCh(ch) {
    var uni = ch.charCodeAt(0);
    var strChineseFirstPY = '';
    //如果不在汉字处理范围之内,返回原字符
    if (uni > 40869 || uni < 19968)
        return ch;
    return strChineseFirstPY.charAt(uni - 19968);
}
//计算字符串长度
Common.strLen = function(str) {
    ///	<summary>
    ///		计算字符串长度包括中英文等
    ///	</summary>


    var realLength = 0, len = str.length, charCode = -1;
    for (var i = 0; i < len; i++) {
        charCode = str.charCodeAt(i);
        if (charCode >= 0 && charCode <= 128) realLength += 1;
        else realLength += 2;
    }
    return realLength;
}

//FormName:表单名，例如：form1
//ElementName:控件名称，例如：txtSmallImage
//ControlType:控件类型，0=select，其他为input
//SaveType:保存文件名类型，1=原文件名，否则用时间重命名
//UpType:上传文件类型：image=图片类型，file=文件类型，media=媒体类型
//=============================以下3个参数用于自定义字段设置。如果非自定义字段设置的上传控件，则不用传参
//ExtType:允许上传的文件类型，例如：jpg|gif|bmp
//MaxSize:允许上传文件大小，单位K，例如：2048（表示2M）
//GetSizeControl:如果上传设置了需要保存文件大小，则填写保存文件大小控件名称，否则不用填写,返回字节数。例如：ImageSize
function InputFile(FormName, ElementName, UpType, ControlType, ExtType, MaxSize, GetSizeControl) {

    var tmp = $("#" + ElementName)[0];
    var urlAddress;

    File = tmp.value;
    if (UpType == "file") {
        urlAddress = "../upfiles/upfile.aspx?pFile=" + File + "&FormName=" + FormName + "&ElementName=" + ElementName + "&ControlType=" + ControlType + "&UpType=" + UpType + "&ExtType=" + ExtType + "&MaxSize=" + MaxSize + "&GetSizeControl=" + GetSizeControl;
    } else {
        urlAddress = "../upfiles/upfile.aspx?pFile=" + File + "&FormName=" + FormName + "&ElementName=" + ElementName + "&ControlType=" + ControlType + "&UpType=" + UpType + "&ExtType=" + ExtType + "&MaxSize=" + MaxSize + "&GetSizeControl=" + GetSizeControl;
    }
    openframe({ title: "文件上传", url: urlAddress, width: '510', height: '200' });
}

//模板风格文件或压缩包上传
