// 存储表单对象变量
var _validate_object_form;
var FormValidate = {
///	<summary>
///		表单验证
///	</summary>

};

Common.getFormValidate = function(formID) {
    ///	<summary>
    ///		获取表单验证对象
    ///	</summary>
    ///	<param name="formID" type="String">
    ///		要验证的表单的ID
    ///	</param>


    _validate_object_form = $("body").find("#" + formID);
    return FormValidate;
};

FormValidate.checkTxtCharNum = function(txtID, maxCharNum, info) {
    ///	<summary>
    ///		验证文本框的最大字符个数
    ///	</summary>
    ///	<param name="txtID" type="String">
    ///		文本框ID
    ///	</param>
    ///	<param name="maxCharNum" type="Integer">
    ///	    最大字符个数
    ///	</param>
    ///	<param name="info" type="String">
    ///	    错误提示信息
    ///	</param>

    if (Common.strLen(Common.trim($(_validate_object_form).find("#" + txtID).val())) > maxCharNum) {
        $(_validate_object_form).find("#" + txtID)[0].focus();
        $(_validate_object_form).find("#" + txtID).parent().after(info);
    }
}

