//获得选中的标题 用于记录日志 by ycl@360hqb.com 2010-5-13
function GetSelectTitle() {
    var titleList = "";
    var objTitle = document.getElementsByName("chkId");

    for (i = 0; i < objTitle.length; i++) {
        if (objTitle[i].checked == true) {
            if (titleList == "")
                titleList = document.getElementById("Title" + objTitle[i].value).innerHTML;
            else
                titleList = titleList + "," + document.getElementById("Title" + objTitle[i].value).innerHTML;
        }
    }
    if (titleList == "") {
        alert({ msg: '请先选择记录，然后再点击操作！', title: '提示信息' })
        return false;
    }
    else
        document.getElementById("hidLogTitle").value = titleList;

    return true;

}

