// 专题中JS
function IsCreateHtml(id, isCreate) {
    if (isCreate == "0") {
        $("#" + id).hide();
        $("#" + id).find("input[type='radio']").each(function() { $(this).removeAttr("checked"); });
    }
    else {
        $("#" + id).show();
        $("#" + id).find("input[type='radio']:first").attr("checked", "checked");
    }
}

// 全选或全部取消
function SelectAll(id, containerID) {
    if ($("#" + id).attr("checked") == true) {
        $("#" + containerID).find("input[type='checkbox']").each(function() {
            $(this).attr("checked", "checked");
        }
                );
    }
    else {
        $("#" + containerID).find("input[type='checkbox']").each(function() {
            $(this).removeAttr("checked");
        }
                );
    }
}

function ConfirmCheckSelect(containerID) {
    var isTrue;
    var titleContent;

    isTrue = false;
    titleContent = "";

    $("#" + containerID).find("input[type='checkbox']").each(function() {
        if ($(this).attr("checked") == true) {

            if (titleContent == "") {
                titleContent = $("#Title" + $(this).val()).text();
            } else {
                titleContent = titleContent + "," + $("#Title" + $(this).val()).text();
            }

            isTrue = true;
        }
    });

    if (!isTrue) {
        alert({ msg: '请选择要操作的记录！', title: '操作提示' })
    }

    $("#hidLogTitle").val(titleContent);
    return isTrue;
}

// 检证是否有选中项
function CheckSelect(containerID) {
    var isTrue;
    isTrue = false;

    $("#" + containerID).find("input[type='checkbox']").each(function() {
        if ($(this).attr("checked") == true) {
            isTrue = true;
        }
    });

    if (!isTrue) {
        alert({ msg: '请选择要操作的记录！', title: '操作提示' })
    }
    return isTrue;
}
// 多选下拉列表全选操作
function multipleSelectAll(id) {
    for (var i = 0; i < document.getElementById(id).length; i++) {
        document.getElementById(id).options[i].selected = true;
    }
}
// 多选下拉列表取消操作
function unMultipleSelectAll(id) {
    for (var i = 0; i < document.getElementById(id).length; i++) {
        document.getElementById(id).options[i].selected = false;
    }
}
// 验证多选下拉列表是否有选择
function checkMultipleSelete(id) {
    var isTrue = false;
    for (var i = 0; i < document.getElementById(id).length; i++) {
        if (document.getElementById(id).options[i].selected == true) {
            isTrue = true;
            break;
        }
    }

    return isTrue;
}

