function reConfirm(obj, message) {
    try {
        HQB_Log_ID = $(obj).attr("RecordID");
    } catch (e) {}
     
    HQB_CommandObject = obj;

    if (!HQB_Log_IsValide) {
        selfconfirm({ msg: message, fn: function(data) { setTitle(data) } })
    }

    return HQB_Log_IsValide;
}

